package com.example.news.dbimport android.app.Applicationimport androidx.room.Roomimport dagger.Moduleimport dagger.Providesimport dagger.hilt.InstallInimport dagger.hilt.components.SingletonComponentimport javax.inject.Singleton@Module@InstallIn(SingletonComponent::class)class DatabaseModule {    @Provides    @Singleton    fun provideNewsDb(application: Application): NewsDb {        return Room.databaseBuilder(application, NewsDb::class.java, "news_database")            .fallbackToDestructiveMigration()            .allowMainThreadQueries()            .build()    }    @Provides    @Singleton    fun provideArticlesDao(newsDb: NewsDb): ArticleDao {        return newsDb.articlesDao()    }}