package com.example.news.networkimport dagger.Moduleimport dagger.Providesimport dagger.hilt.InstallInimport retrofit2.Retrofitimport retrofit2.adapter.rxjava2.RxJava2CallAdapterFactoryimport retrofit2.converter.gson.GsonConverterFactoryimport javax.inject.Singleton@Module@InstallInobject RetrofitClient {    @Provides    @Singleton    fun getRetrofit(baseUrl: String): Retrofit {        return Retrofit.Builder()            .addConverterFactory(GsonConverterFactory.create())            .addCallAdapterFactory(RxJava2CallAdapterFactory.create())            .baseUrl(baseUrl)            .build()    }}