package com.example.news.repositoryimport com.example.news.db.ArticleDaoimport com.example.news.models.Articleimport com.example.news.models.NewsResponseimport com.example.news.network.Commonimport io.reactivex.Observableimport javax.inject.Injectclass Repository {    private var articleDao: ArticleDao    private lateinit var common: Common    @Inject    constructor(articleDao: ArticleDao, common: Common) {        this.articleDao = articleDao        this.common = common    }    fun getNews(country: String, apiKey: String): Observable<NewsResponse> {        return common.retrofitService.getNews(country, apiKey)    }    fun saveArticles(articleList: List<Article>) {        articleDao.saveArticles(articleList)    }}